$(function () {
    //全选（checkall）  三个小的（j-checkbox)
    $(".checkall").change(function () {
        $('.j-checkbox,.checkall').prop("checked", $(this).prop("checked"));
        if ($(this).prop("checked")) {
            //让所有商品添加类名
            $(".cart-item").addClass("check-cart-item");
        } else {
            $(".cart-item").removeClass("check-cart-item");
        }
    });
    $(".j-checkbox").change(function () {
        if ($(".j-checkbox:checked").length === $(".j-checkbox").length) {
            $(".checkall").prop("checked", true);
        }
        else {
            $(".checkall").prop("checked", false);
        }
        if ($(this).prop("checked")) {
            //让当前商品添加类名
            $(this).parents(".cart-item").addClass("check-cart-item");
        } else {
            $(this).parents(".cart-item").removeClass("check-cart-item");
        }
    })
    //增加数量，取兄弟文本框的值
    $(".increment").click(function () {
        var n = $(this).siblings(".itxt").val();
        n++;
        $(this).siblings(".itxt").val(n);
        var m = $(this).parents(".p-num").siblings(".p-price").html();
        m = m.substr(1);
        $(this).parents(".p-num").siblings(".p-sum").html('￥' + (n * m).toFixed(2));
        getsum();
    })
    //减去数量，判断不能小于1
    $(".decrement").click(function () {
        var n = $(this).siblings(".itxt").val();
        if (n == 1) {
            return false;
        }
        n--;
        $(this).siblings(".itxt").val(n);
        var m = $(this).parents(".p-num").siblings(".p-price").html();
        m = m.substr(1);
        $(this).parents(".p-num").siblings(".p-sum").html('￥' + (m / n).toFixed(2));
        getsum();
    })
    $(".itxt").change(function () {
        var n = $(this).val();
        var p = $(this).parents(".p-num").siblings(".p-price").html();
        p = p.substr(1);
        $(this).parents(".p-num").siblings(".p-sum").html("￥" + (n * p).toFixed(2));
        getsum();
    })
    $(".p-action a").click(function () {
        $(this).parents(".cart-item").remove();//删除多个
        getsum();
    });
    $(".remove-batch").click(function () {
        $(".j-checkbox:checked").parents(".cart-item").remove();//删除选中
        getsum();
    });
    $(".clear-all").click(function () {
        $(".cart-item-list").empty();//清理购物车
        getsum();
    })
    getsum();
    function getsum() {
        var count = 0;
        var money = 0;
        $(".itxt").each(function (i, ele) {
            count += parseInt($(ele).val());
        });
        $(".amount-sum em").text(count);
        $('.p-sum').each(function (i, ele) {
            money += parseFloat($(ele).text().substr(1));
        });
        $(".price-sum em").text("￥" + money.toFixed(2));
    }
})